import React, { useState } from "react";
import { DateRange } from 'react-date-range';


import './Header.css'
function Header() {
  const [isSalender, setIsSalender] = useState(false)
  const [dateRange, setDateRange] = useState([
    {
      startDate: new Date(),
      endDate: new Date(),
      key: 'selection'
    }
  ])

  let startDate = dateRange[0].startDate
  let endDate = dateRange[0].endDate

  const clickSearch = () => {
    location.replace('/search');
  }

  const handleSelect = (date) => {
    console.log(date);
    setDateRange([date.selection])
    console.log(dateRange);
  }

  const handleClick = () => {
    setIsSalender(isSalender => !isSalender)
  }

  return (
    <div style={{ position: 'relative' }} >

      <h2 className='title-header'>A lifetime of discounts? It's Genius.</h2>
      <div className='desc-header'>Get rewarded for your travals - unlock instant savings of 10% or more with a free account </div>
      <button className='btn-header'>Sign in / Registor</button>
      {isSalender &&
        <div className='dateRange-Header'>
          <DateRange
            editableDateInputs={true}
            moveRangeOnFirstSelection={false}
            className="date"
            minDate={new Date()}
            onChange={handleSelect}
            ranges={dateRange}
            dateFormat="DD/MM/YYYY"
          />
        </div>}
      <div className='search-header'>
        <div>
          <i className='fa fa-bed'></i>
          <input id='place' type='text' placeholder='Where are you going?' />
        </div>
        <div onClick={handleClick} style={{ position: 'relative' }}>
          <i className='fa fa-calendar'></i>
          <input
            className='input-dateRange'

            readOnly
            size='24'
            value={`${startDate.getMonth() + 1}/${startDate.getDate()}/${startDate.getFullYear()}  to  ${endDate.getMonth() + 1}/${endDate.getDate()}/${endDate.getFullYear()} 
            `}
          />
        </div>
        <div>
          <i className='fa fa-female'></i>
          <input id='noAdult' type='text' placeholder='1' />
          adult -
          <input id='noAChildren' type='text' placeholder='0' />
          children -
          <input id='noRoom' type='text' placeholder='1' />
          room
        </div>
        <button onClick={clickSearch} className='btn-search'>Search</button>
      </div>

    </div>
  )
}

export default Header
