import { createSlice, configureStore } from '@reducjs/toolkit'


const initState = { counter: 0, showCounter: true }

const counterSlice = createSlice({
  //tên của slice
  name: 'couter',
  //gái trị khởi tạo
  initState,
  //reducers chứa các method thay cho sw case
  // và có thể thay đổi trục tiếp thứ cần thay đổi ở giá trị khởi tạo
  // không  cần tạo mới hoàn toàn giống như store của redux
  reducers: {
    //các phương thức có 2 dối số : 1 là state,2 là action
    //action đc chho vào và sử dụng khi sử dụng data từ dispatch trả về 
    increment(state, action) {
      state.counter++
    },
    decrement(state, action) {
      state.counter--
    },
  }
})
const counterReducer = (state = initState, action) => {
  if (action.type === 'increment') {
    return {
      counter: state.counter + 1,
      showCounter: state.showCounter,
    }
  }
  if (action.type === 'decrement') {
    return {
      counter: state.counter - 1,
      showCounter: state.showCounter,
    }
  }
  if (action.type === 'toggle') {
    return {
      showCounter: !state.showCounter,
      counter: state.counter
    }
  }
  return state
}

const store = createStore(counterReducer)

export default store
